import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';

function AIChatSection() {
    const { user, queryAI } = useAuth();
    const [query, setQuery] = useState('');
    const [response, setResponse] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!query.trim()) return;

        setLoading(true);
        setError(null);
        try {
            const result = await queryAI(query);
            setResponse(result.response);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    if (!user) {
        return (
            <div className="flex flex-col items-center justify-center min-h-[400px] p-6">
                <h2 className="text-2xl font-semibold mb-4">Sign in to use ConuAI</h2>
                <p className="text-gray-600 mb-6">
                    Please sign in to ask questions and get personalized assistance.
                </p>
                <Link
                    to="/signin"
                    className="inline-flex items-center justify-center rounded-md bg-[oklch(44.889%_0.15545_-73.341)] px-4 py-2 text-sm font-medium text-white hover:bg-[oklch(44.889%_0.15545_-73.341)]/90"
                >
                    Sign In
                </Link>
            </div>
        );
    }

    return (
        <div className="max-w-3xl mx-auto p-6">
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label htmlFor="query" className="block text-sm font-medium text-gray-700">
                        Ask ConuAI
                    </label>
                    <div className="mt-1">
                        <textarea
                            id="query"
                            name="query"
                            rows={4}
                            className="block w-full rounded-md border border-gray-300 shadow-sm focus:border-[oklch(44.889%_0.15545_-73.341)] focus:ring-[oklch(44.889%_0.15545_-73.341)] sm:text-sm p-2"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            placeholder="Ask anything about Concordia University..."
                        />
                    </div>
                </div>
                
                <button
                    type="submit"
                    disabled={loading}
                    className="inline-flex items-center justify-center rounded-md bg-[oklch(44.889%_0.15545_-73.341)] px-4 py-2 text-sm font-medium text-white hover:bg-[oklch(44.889%_0.15545_-73.341)]/90 disabled:opacity-50"
                >
                    {loading ? 'Thinking...' : 'Ask Question'}
                </button>
            </form>

            {error && (
                <div className="mt-4 p-4 bg-red-50 text-red-700 rounded-md">
                    {error}
                </div>
            )}

            {response && (
                <div className="mt-6 p-4 bg-gray-50 rounded-md">
                    <h3 className="text-lg font-medium mb-2">Response:</h3>
                    <p className="text-gray-700 whitespace-pre-wrap">{response}</p>
                </div>
            )}
        </div>
    );
}

export default AIChatSection;
